// ุชุญุฏูุฏ ุงูุฃุณุฆูุฉ ุงูุฎุงุตุฉ ุจุงููุณู ุงูุญุงูู ููุท
let questions = allQuestions.slice(sectionStarts[currentSection - 1], sectionStarts[currentSection - 1] + QUESTIONS_PER_SECTION);

/*
 * ============================================
 * ุฏุงูุฉ updateQuestion (ุงูููุจ ุงููุงุจุถ ููุนุฑุถ)
 * ============================================
 */
function updateQuestion() {
  const q = questions[currentIndex];
  const sectionTitleElement = document.getElementById("section-title");
  const paragraphBoxElement = document.getElementById("paragraph-box");
  const submitBtn = document.getElementById("submit-section-btn");

  // 1. ุชุญุฏูุฏ ุนููุงู ุงููุณู ุงูุฑุฆูุณู
  let currentSectionName = "";
  if (mode === 'real') {
    currentSectionName = `ุงููุณู ${currentSection} ูู ${totalSections}`;
  } else {
    currentSectionName = `ุงูุงุฎุชุจุงุฑ ุงูุณุฑูุน (ุงููุณู 1)`;
  }

  // 2. ุฅุนุฏุงุฏ ูุญุชูู ุนููุงู ุงููุณู ุงูุฑุฆูุณู
  sectionTitleElement.innerHTML = `<h2>${currentSectionName}</h2>`;
  
  // 3. ุนุฑุถ ุนููุงู ุงูููุฑุฉ (Header)
  const prevHeader = currentIndex > 0 ? questions[currentIndex - 1].header : null;
  if (q.header && q.header !== prevHeader) {
      sectionTitleElement.innerHTML += `<h3 style="color: #023e8a; margin-top: 10px;">${q.header}</h3>`;
  }

  // 4. ุนุฑุถ ุงูููุฑุฉ ุงูุทูููุฉ (Paragraph) ูู ุงูุนูุตุฑ ุงููุฎุตุต ููุง
  const prevParagraph = currentIndex > 0 ? questions[currentIndex - 1].paragraph : null;
  if (q.paragraph) {
      if (q.paragraph !== prevParagraph) {
          paragraphBoxElement.innerHTML = `<div style="background-color: #f0f9ff; padding: 15px; border-radius: 8px; text-align: justify;"><p>${q.paragraph}</p></div>`;
          paragraphBoxElement.style.display = 'block';
      } else {
          paragraphBoxElement.style.display = 'block';
      }
  } else {
      paragraphBoxElement.innerHTML = '';
      paragraphBoxElement.style.display = 'none';
  }

  // 5. ุนุฑุถ ุฑูู ุงูุณุคุงู
  sectionTitleElement.innerHTML += `<p>ุงูุณุคุงู ${currentIndex + 1} ูู ${questions.length}</p>`;

  // 6. ุนุฑุถ ูุต ุงูุณุคุงู ูุงูุฎูุงุฑุงุช ูู innerHTML
  document.getElementById("question-text").textContent = q.text;

  let answersHTML = "";
  q.options.forEach((opt, i) => {
    if (opt) {
        answersHTML += `<label><input type="radio" name="answer" value="${i}" ${q.answer === i ? "checked" : ""}> ${opt}</label>`;
    }
  });
  document.getElementById("answers").innerHTML = answersHTML;

  // 7. ุชุญุฏูุซ ูุต ุฒุฑ ุงูุชุณููู/ุงูุฅููุงุก
  if (currentSection < totalSections) {
      submitBtn.textContent = `โ ุชุณููู ุงููุณู ${currentSection} ูุงูุงูุชูุงู ููุชุงูู`;
      submitBtn.onclick = endSection;
  } else {
      submitBtn.textContent = `๐ ุฅููุงุก ุงูุงูุชุญุงู`;
      submitBtn.onclick = finishExam; // ุงููุณู ุงูุฃุฎูุฑ ูููู ุงูุงูุชุญุงู ูุจุงุดุฑุฉ
  }
}

function saveAnswer() {
  const selected = document.querySelector("input[name='answer']:checked");
  questions[currentIndex].answer = selected ? parseInt(selected.value) : null;
}

function nextQuestion() {
  saveAnswer();
  if (currentIndex < questions.length - 1) {
    currentIndex++;
    updateQuestion();
  } else if (currentIndex === questions.length - 1) {
    // ุฅุฐุง ูุตู ูุขุฎุฑ ุณุคุงู ูู ุงููุณู (ุงูุณุคุงู 25)ุ ููุชูู ูุดุงุดุฉ ุงููุฑุงุฌุนุฉ ูุจุงุดุฑุฉ
    reviewSection();
  }
}

function prevQuestion() {
  saveAnswer();
  if (currentIndex > 0) {
    currentIndex--;
    updateQuestion();
  }
}

function markQuestion() {
  questions[currentIndex].marked = !questions[currentIndex].marked; 
  alert(questions[currentIndex].marked ? "โญ ุชู ูุถุน ุนูุงูุฉ ูุฑุฌุนูุฉ" : "โ ุชู ุฅุฒุงูุฉ ุงูุนูุงูุฉ ุงููุฑุฌุนูุฉ");
}

function reviewSection() {
  saveAnswer();
  // ุญูุธ ุญุงูุฉ ุงููุณู ูุจู ุงูุงูุชูุงู ูููุฑุงุฌุนุฉ
  localStorage.setItem(`section_questions_${currentSection}`, JSON.stringify(questions));

  let html = `<h2>ูุฑุงุฌุนุฉ ุงููุณู ${currentSection}</h2><ul>`;
  questions.forEach((q, i) => {
    let status = q.answer !== null ? "โ ูุฌุงุจ" : "โ ุบูุฑ ูุฌุงุจ";
    if (q.marked) status += " โญ ูุฑุฌุนู";
    html += `<li>ุณุคุงู ${i + 1}: ${status} <button onclick="window.location.href='quiz.html?section=${currentSection}&returnTo=${i}'">๐</button></li>`;
  });
  
  // ุชุญุฏูุฏ ูุต ุฒุฑ ุงูุฅููุงุก ุจูุงุกู ุนูู ุงููุณู
  const endButtonText = (currentSection < totalSections) ? 'โ ุชุณููู ุงููุณู ูุงูุงูุชูุงู' : '๐ ุฅููุงุก ุงูุงุฎุชุจุงุฑ';

  html += `</ul>
    <button onclick="window.location.href='quiz.html?section=${currentSection}&returnTo=0'">๐ ุงูุนูุฏุฉ ูุฃูู ุณุคุงู</button>
    <button onclick="endSection()">${endButtonText}</button>`;
    
  // ุงุณุชุจุฏุงู ูุญุชูู ุงูุฌุณู ุจุดุงุดุฉ ุงููุฑุงุฌุนุฉ
  document.body.innerHTML = html;
}

function goTo(index) {
  saveAnswer();
  // ุญูุธ ุญุงูุฉ ุงููุณู ูุจู ุงูุงูุชูุงู
  localStorage.setItem(`section_questions_${currentSection}`, JSON.stringify(questions));
  // ุฅุนุงุฏุฉ ุชูุฌูู ูุน ุชุญุฏูุฏ ุงูุณุคุงู ุงููุฑุงุฏ ุงูุฑุฌูุน ุฅููู
  window.location.href = `quiz.html?section=${currentSection}&returnTo=${index}`;
}

function chooseQuestion() {
  let num = prompt("ุฃุฏุฎู ุฑูู ุงูุณุคุงู:");
  if (num && !isNaN(num) && num >= 1 && num <= questions.length) {
    goTo(parseInt(num) - 1);
  }
}

function endSection() {
  saveAnswer();
  
  // ุญูุธ ุฃุณุฆูุฉ ุงููุณู ุงูุญุงูู
  localStorage.setItem(`section_questions_${currentSection}`, JSON.stringify(questions));

  if (currentSection < totalSections) {
    // ุงูุงูุชูุงู ูููุณู ุงูุชุงูู
    localStorage.setItem("section", currentSection + 1);
    window.location.href = "quiz.html";
  } else {
    // ุฅููุงุก ุงูุงูุชุญุงู ุจุงููุงูู (ุงููุณู ุงูุฃุฎูุฑ)
    finishExam();
  }
}

function finishExam() {
  saveAnswer();
  let errors = [];
  let totalCorrectAnswers = 0;
  let totalQuestionsCount = 0;
  const optionLabels = ["ุฃ", "ุจ", "ุฌ", "ุฏ"];

  for (let i = 1; i <= totalSections; i++) {
      const savedSection = localStorage.getItem(`section_questions_${i}`);
      if (savedSection) {
          const sectionQuestions = JSON.parse(savedSection);
          totalQuestionsCount += sectionQuestions.length;
          
          sectionQuestions.forEach((q, indexInSection) => {
              // 1. ุญุณุงุจ ุงูุฅุฌุงุจุงุช ุงูุตุญูุญุฉ
              if (q.answer !== null && q.answer === q.correct) {
                  totalCorrectAnswers++;
              } else if (q.answer !== q.correct) {
                  // 2. ุชุณุฌูู ุงูุฃุฎุทุงุก (ุฅุฐุง ูู ูุชู ุงูุฅุฌุงุจุฉ ุฃู ูุงูุช ุงูุฅุฌุงุจุฉ ุฎุงุทุฆุฉ)
                  const questionNumber = indexInSection + 1;
                  errors.push({
                      section: i,
                      question: questionNumber,
                      text: q.text,
                      userAnswer: q.answer !== null ? q.options[q.answer] : "ูู ุชุชู ุงูุฅุฌุงุจุฉ",
                      correctAnswer: q.options[q.correct],
                      correctLabel: q.options[q.correct] ? optionLabels[q.correct] : 'ุบูุฑ ูุญุฏุฏ' 
                  });
              }
          });
      }
  }

  const finalScore = (totalCorrectAnswers * 0.83).toFixed(2);
  
  // ุชุฎุฒูู ุชูุฑูุฑ ุงูุฃุฎุทุงุก ุงูููุตู
  localStorage.setItem("quizResults", JSON.stringify({
    score: finalScore,
    correct: totalCorrectAnswers,
    total: totalQuestionsCount,
    errors: errors,
    totalSections: totalSections
  }));
  
  // ุชูุธูู ุงูุชุฎุฒูู ุงูุฎุงุต ุจุงูุฃูุณุงู
  for (let i = 1; i <= totalSections; i++) {
      localStorage.removeItem(`section_questions_${i}`);
  }

  window.location.href = "thankyou.html";
}

// ** ููุทู ุงูุชุญููู ูุงูุชุฎุฒูู ูููุณู ุงูุญุงูู (ุนูุฏ ุชุญููู ุงูุตูุญุฉ) **
document.addEventListener('DOMContentLoaded', () => {
    // 1. ุชุญุฏูุฏ ุจุฏุงูุฉ ุงูุฃุณุฆูุฉ ูููุณู ุงูุญุงูู ูู ุงููุตูููุฉ ุงููููุฉ
    const start = sectionStarts[currentSection - 1];
    const end = start + QUESTIONS_PER_SECTION;
    questions = allQuestions.slice(start, end);
    
    // 2. ูุญุงููุฉ ุงุณุชุนุงุฏุฉ ุงูุฃุณุฆูุฉ ูู ุงูุชุฎุฒูู ุงููุญูู (ููุญุงูุฉ ุงููุญููุธุฉ)
    const savedSectionQuestions = localStorage.getItem(`section_questions_${currentSection}`);
    if (savedSectionQuestions) {
        questions = JSON.parse(savedSectionQuestions);
    } 
    
    // 3. ุงูุชุญูู ูู ูุฌูุฏ ุฑูู ุณุคุงู ูุญุฏุฏ ููุนูุฏุฉ ุฅููู ูู URL
    const urlParams = new URLSearchParams(window.location.search);
    const returnToIndex = urlParams.get('returnTo');
    if (returnToIndex !== null) {
        currentIndex = parseInt(returnToIndex);
    } else {
        currentIndex = 0;
    }
    
    updateQuestion();

    // 4. ุฅุฒุงูุฉ ุฒุฑ ุฅููุงุก ุงูุงูุชุญุงู ุงููุฏูู (end-exam) ุฅุฐุง ูุฌุฏุ ูุถูุงู ุงุณุชุฎุฏุงู ุฒุฑ ูุงุญุฏ
    const oldEndExamBtn = document.getElementById("end-exam");
    if (oldEndExamBtn) {
        oldEndExamBtn.remove();
    }

    // 5. ูุชู ูุณุญ ูุนุงููุงุช URL ูุถูุงู ุจุฏุงูุฉ ูุธููุฉ ูู ุงููุฑุฉ ุงููุงุฏูุฉ
    window.history.replaceState({}, document.title, "quiz.html");

});


