// ุฏุงูุฉ ุชูุณูู ุงููุต (ูููุณูุฑ ูุงูุฌุฐูุฑ)
function formatText(text) {
  // ุชุจุฏูู ุงููุณูุฑ LaTeX ุฅูู ุนุฑุถ HTML ุจุณูุท (ูููู ุชุนุฏููู ููููู ุฃุฌูู ุจุงุณุชุฎุฏุงู CSS ุฃู MathJax ุฅุฐุง ุชููุฑ)
  return text.replace(/(\\frac{([^{}]+)}{([^{}]+)})/g, (match, p1, numerator, denominator) => {
      return `<span style="font-size: 1.2em; vertical-align: middle;">${numerator}&frasl;${denominator}</span>`;
  })
  .replace(/\\sqrt{([^{}]+)}/g, (match, p1) => `&radic;<span style="text-decoration:overline">${p1}</span>`);
}

function updateQuestion() {
  const q = questions[currentIndex];
  const sectionTitleElement = document.getElementById("section-title");

  // 1. ุชุญุฏูุฏ ุงููุณู ุงูุญุงูู ููุทุงู ุงูุณุคุงู ุถูู ุงููุณู (ูููุญุงูู ุงููุงูุนู)
  let currentSectionRange = sectionRanges[section - 1];
  let questionIndexInSection = (currentIndex - currentSectionRange.start) + 1;
  let totalQuestionsInSection = currentSectionRange.end - currentSectionRange.start + 1;

  // 2. ุนุฑุถ ุนููุงู ุงููุณู ุงูุนุงู
  if (mode === 'real') {
      sectionTitleElement.innerHTML = `<h2>ุงููุณู ${section}: ${currentSectionRange.name}</h2>`;
  } else {
      sectionTitleElement.innerHTML = `<h2>ุงูุงุฎุชุจุงุฑ ุงูุณุฑูุน: ${q.header || 'ุฃุณุฆูุฉ ูุชููุนุฉ'}</h2>`;
  }
  
  // 3. ุนุฑุถ ุนููุงู ุงูููุฑุฉ (Header) ูุฑูู ุงูุณุคุงู
  sectionTitleElement.innerHTML += `<h3 style="color: #023e8a; margin-top: 10px;">${q.header || ''}</h3>`;
  sectionTitleElement.innerHTML += `<p>ุงูุณุคุงู ${currentIndex + 1} ูู ${questions.length}</p>`;

  // 4. ุนุฑุถ ูุต ุงูุณุคุงู ูุชูุณูู ุงููุณูุฑ
  document.getElementById("question-text").innerHTML = formatText(q.text);

  let answersHTML = "";
  q.options.forEach((opt, i) => {
    if (opt) {
        answersHTML += `<label><input type="radio" name="answer" value="${i}" ${q.answer === i ? "checked" : ""}> ${formatText(opt)}</label>`;
    }
  });
  document.getElementById("answers").innerHTML = answersHTML;

  // ุฒุฑ ุฅููุงุก ุงูุงูุชุญุงู ูู ุขุฎุฑ ุณุคุงู ููุท
  const endExamBtn = document.getElementById("end-exam");
  if (currentIndex === questions.length - 1) {
    endExamBtn.style.display = "block";
  } else {
    endExamBtn.style.display = "none";
  }
}

function saveAnswer() {
  const selected = document.querySelector("input[name='answer']:checked");
  questions[currentIndex].answer = selected ? parseInt(selected.value) : null;
}

function nextQuestion() {
  saveAnswer();
  if (currentIndex < questions.length - 1) {
    currentIndex++;
    updateQuestion();
  }
}

function prevQuestion() {
  saveAnswer();
  if (currentIndex > 0) {
    currentIndex--;
    updateQuestion();
  }
}

function markQuestion() {
  questions[currentIndex].marked = !questions[currentIndex].marked; 
  alert(questions[currentIndex].marked ? "โญ ุชู ูุถุน ุนูุงูุฉ ูุฑุฌุนูุฉ" : "โ ุชู ุฅุฒุงูุฉ ุงูุนูุงูุฉ ุงููุฑุฌุนูุฉ");
}

function reviewSection() {
  saveAnswer();
  let html = `<h2>ูุฑุงุฌุนุฉ ุงููุณู ${section}</h2><ul>`;
  questions.forEach((q, i) => {
    let status = q.answer !== null ? "โ ูุฌุงุจ" : "โ ุบูุฑ ูุฌุงุจ";
    if (q.marked) status += " โญ ูุฑุฌุนู";
    html += `<li>ุณุคุงู ${i + 1}: ${status} <button onclick="goTo(${i})">๐</button></li>`;
  });
  html += `</ul>
    <button onclick="goTo(0)">๐ ุงูุนูุฏุฉ ูุฃูู ุณุคุงู</button>
    <button onclick="chooseQuestion()">๐ ุงูุนูุฏุฉ ูุณุคุงู ูุญุฏุฏ</button>
    <button onclick="endSection()">โ ุฅููุงุก ุงููุณู</button>`;
  document.body.innerHTML = html;
}

function goTo(index) {
  saveAnswer();
  localStorage.setItem("returnTo", index);
  localStorage.setItem("questions", JSON.stringify(questions));
  location.reload();
}

function chooseQuestion() {
  let num = prompt("ุฃุฏุฎู ุฑูู ุงูุณุคุงู:");
  if (num && !isNaN(num) && num >= 1 && num <= questions.length) {
    goTo(parseInt(num) - 1);
  }
}

function endSection() {
  saveAnswer();
  if (mode === "real" && section < totalSections) {
    localStorage.setItem("section", section + 1);
    location.reload();
  } else {
    finishExam();
  }
}

function finishExam() {
  saveAnswer();
  let correctAnswers = 0;
  
  questions.forEach(q => {
    if (q.answer !== null && q.answer === q.correct) {
      correctAnswers++;
    }
  });

  const totalScore = (correctAnswers * 0.83).toFixed(2);
  const totalQuestions = questions.length;
  
  localStorage.setItem("quizResults", JSON.stringify({
    score: totalScore,
    correct: correctAnswers,
    total: totalQuestions
  }));

  window.location.href = "thankyou.html";
}

