function chooseQuestion() {
  let num = prompt("ุฃุฏุฎู ุฑูู ุงูุณุคุงู:");
  if (num && !isNaN(num) && num >= 1 && num <= questions.length) {
    goTo(parseInt(num) - 1);
  }
}

function endSection() {
  saveAnswer();
  if (mode === "real" && section < totalSections) {
    localStorage.setItem("section", section + 1);
    // ูุณุญ ุงูุฃุณุฆูุฉ ูุงูููุช ุงูุญุงูููู ููุจุฏุก ุจุจูุงูุงุช ุฌุฏูุฏุฉ ูููุณู ุงูุชุงูู
    localStorage.removeItem("questions");
    localStorage.removeItem("timeLeft");
    location.reload();
  } else {
    finishExam();
  }
}

function finishExam() {
  saveAnswer();
  
  // 1. ุญุณุงุจ ุงูุฏุฑุฌุงุช ูุชุฌููุน ุงูุฃุฎุทุงุก
  let score = 0;
  let correctCount = 0;
  let incorrectCount = 0;
  let unattemptedCount = 0;
  let reviewQuestions = [];
  
  questions.forEach(q => {
    // ุงูุชุญูู ูู ุงูุฅุฌุงุจุฉ (ุฅุฐุง ูุงู ููุงู ุฅุฌุงุจุฉ)
    if (q.answer !== null) {
      if (q.answer === q.correct) {
        correctCount++;
        // ุฅุถุงูุฉ ูููุฉ ุงูุณุคุงู (0.83)
        score += q.value || 0; 
      } else {
        incorrectCount++;
        // ุฅุถุงูุฉ ุงูุณุคุงู ุงูุฎุงุทุฆ ุฅูู ูุงุฆูุฉ ุงููุฑุงุฌุนุฉ
        reviewQuestions.push(q);
      }
    } else {
      unattemptedCount++;
      // ุฅุถุงูุฉ ุงูุณุคุงู ุงูุฐู ูู ูุชู ุงูุฅุฌุงุจุฉ ุนููู ุฅูู ูุงุฆูุฉ ุงููุฑุงุฌุนุฉ
      reviewQuestions.push(q);
    }
  });

  // 2. ุชุฎุฒูู ุงููุชุงุฆุฌ ุงูููุงุฆูุฉ ููุงุฆูุฉ ุงููุฑุงุฌุนุฉ
  localStorage.setItem("finalScore", score.toFixed(2));
  localStorage.setItem("correctCount", correctCount);
  localStorage.setItem("incorrectCount", incorrectCount);
  localStorage.setItem("unattemptedCount", unattemptedCount);
  localStorage.setItem("reviewQuestions", JSON.stringify(reviewQuestions));
  
  // ูุณุญ ุจูุงูุงุช ุงูุฌูุณุฉ (ุงูุฃุณุฆูุฉ ูุงูููุช ุงูุญุงูููู) ุงุณุชุนุฏุงุฏูุง ูููุฑุงุฌุนุฉ
  localStorage.removeItem("questions");
  localStorage.removeItem("timeLeft");
  
  // 3. ุงูุงูุชูุงู ุฅูู ุตูุญุฉ ุงููุฑุงุฌุนุฉ
  window.location.href = "review.html";
}

